import './App.css';
import './Form.css'
import React from 'react';

class Form extends React.Component{
    constructor(props){
        super(props);
        this.state={game:{name:'',genre:'',studio:''}};
    }

    handleSubmit(){
        console.log(JSON.stringify(this.state));
    }

    render(){
        return(
            <div>
                <form id='Form' onSubmit={this.handleSubmit}>
                    <div>
                        <label className='Form-label'>Name</label>
                        <input type="text" value={this.state.name} placeholder='Name'/>
                    </div>
                        <br/>
                    <div>
                        <label className='Form-label'>Genre</label>
                        <input type="text" value={this.state.genre} placeholder='Genre'/>
                    </div>
                        <br/>
                    <div>
                        <label className='Form-label'>Studio</label>
                        <input type="text" value={this.state.studio} placeholder='Studio'/>
                    </div>
                    <br/>
                    <div>
                        <input type="submit" value="Submit"/>
                    </div>
                </form>
            </div>
        );
    }
}


export default Form;
